INSERT INTO [DOCUMENT_REMARKS] ( DOC_REMARK_CODE, ROWVERSION, CREATE_BY, CREATE_DATE, LASTUPD_BY, LASTUPD_DATE, DOC_REMARK_GROUP, DOC_REMARK) VALUES (9, 		0, 'SYSTEM', CURRENT_TIMESTAMP, 'SYSTEM', CURRENT_TIMESTAMP, 'ACD',		N'The captioned ship has been de-registered from the Hong Kong Shipping Registry. An updated ‘Continuous Synopsis Record (CSR)’ is issued to reflect the updated status. ' + CHAR(13)+CHAR(10) + 'In accordance to Annex A23/Res.959 of Reg 5 in SOLAS Chapter XI-1, this office is ready to forward all the issued CSRs to the new flag state of the subject ship. ' + CHAR(13)+CHAR(10) + 'You are hereby requested to provide us the email or fax address (Preferable email address) of the new flag state. ' + CHAR(13)+CHAR(10) + 'Should you require any further information, please feel free to contact the undersigned on email address ‘cm_li@mardep.gov.hk’.'  );
INSERT INTO [DOCUMENT_REMARKS] ( DOC_REMARK_CODE, ROWVERSION, CREATE_BY, CREATE_DATE, LASTUPD_BY, LASTUPD_DATE, DOC_REMARK_GROUP, DOC_REMARK) VALUES (10, 		0, 'SYSTEM', CURRENT_TIMESTAMP, 'SYSTEM', CURRENT_TIMESTAMP, 'ACD',		N'S83. Grant of new certificate of registry' + CHAR(13)+CHAR(10) + '(1) the owner, demise charterer or master shall deliver the old certificate of registry, or cause the certificate to be delivered, to the Registrar—' + CHAR(13)+CHAR(10) + '(a) forthwith, if the ship is in Hong Kong; or' + CHAR(13)+CHAR(10) + '(b) if the ship is not in Hong Kong, as soon as practicable and in any case within a period of 30 days after the date on which the new certificate is granted.' + CHAR(13)+CHAR(10) + '(2) Upon the grant of a new certificate of registry of a registered ship under subsection' + CHAR(13)+CHAR(10) + '3) An owner, demise charterer or master who, without reasonable excuse, fails to comply with subsection (2) commits an offence and is liable to a fine of $5,000.'  );
